package week2_Homework;

public class Pattern_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		int j;
		
		for(i=1;i<10;i++){
			
			if(i%2!=0){
			
			for(j=1;j<=i;j++){
				
				System.out.print("*");			
			}
			System.out.println('\n');
			}
		}
		}

	}

